import './global.css';
import './App.css';
import './Sidebar.css';
import './Main.css';

// navigator.geolocation.getCurrentPosition -> se puser aqui vai ser executado uma unica vez no ciclo de vida da aplicação

function App() {
  const [latitude, setLatitude] = useState('');
  const [longitude, setlongitude] = useState('');

  useEffect(() => {
    navigator.geolocation.getCurrentPosition( // Em algum momento, o navegador vai me perguntar se eu permito que ele pegue minha localização. A permissão só precisa ser dada uma vez
      (position) => { 
        const {latitude, longitude} = position.coords;
        console.log(position);
      },

      (err) => {
        console.log(err)
      },

      {
        timeout: 30000,
      }

    )  
  }, []);





  return(
    <div id="app">
      <aside>
        <strong>Cadastrar</strong>
        <form>
          <div className="input-block">
            <label htmlFor="github_username" >Usuário do Github</label>
            <input name="github_username" id="github_username" required />
          </div>

          <div className="input-block">
            <label htmlFor="techs" >Tecnologias</label>
            <input name="techs" id="techs" required />
          </div>

          <div className="input-group">
            <div className="input-block">
              <label htmlFor="latitude" >Latitude</label>
              <input name="latitude" id="latitude" required />
            </div>

            <div className="input-block">
              <label htmlFor="longitude" >Longitude</label>
              <input name="longitude" id="longitude" required />
            </div>
          </div>
          <button type="submit">Salvar</button>
        </form>

      </aside>

      <main>
        <ul>
          <li className="dev-item">
            <header>
              <img src="https://avatars0.githubusercontent.com/u/18712297?v=4" alt="Eduardo" />
              <div className="user-info">
                <strong>Eduardo Mirolli</strong>
                <span>ReactJS, React Native, Node.js</span>
              </div>
            </header>
            <p>Computer Science Graduation in progress...</p>
            <a href="https://github.com/EdZM">Acessar perfil no github</a>
          </li>
          <li className="dev-item">
            <header>
              <img src="https://avatars0.githubusercontent.com/u/18712297?v=4" alt="Eduardo" />
              <div className="user-info">
                <strong>Eduardo Mirolli</strong>
                <span>ReactJS, React Native, Node.js</span>
              </div>
            </header>
            <p>Computer Science Graduation in progress...</p>
            <a href="https://github.com/EdZM">Acessar perfil no github</a>
          </li>
          <li className="dev-item">
            <header>
              <img src="https://avatars0.githubusercontent.com/u/18712297?v=4" alt="Eduardo" />
              <div className="user-info">
                <strong>Eduardo Mirolli</strong>
                <span>ReactJS, React Native, Node.js</span>
              </div>
            </header>
            <p>Computer Science Graduation in progress...</p>
            <a href="https://github.com/EdZM">Acessar perfil no github</a>
          </li>
          <li className="dev-item">
            <header>
              <img src="https://avatars0.githubusercontent.com/u/18712297?v=4" alt="Eduardo" />
              <div className="user-info">
                <strong>Eduardo Mirolli</strong>
                <span>ReactJS, React Native, Node.js</span>
              </div>
            </header>
            <p>Computer Science Graduation in progress...</p>
            <a href="https://github.com/EdZM">Acessar perfil no github</a>
          </li>


        </ul>



      </main>
    </div>  

  );


}



export default App;
